import React, { useEffect, useState } from 'react'

function UserProfile() {

    const [token, setToken] = useState("");
    const [username, setUsername] = useState("");

    useEffect(() => {
        const sessionTok = localStorage.getItem("token")
        if(sessionTok)
            setToken(JSON.parse(sessionTok))
    }, []);

    useEffect(() => {
        const checkToken = async () =>{
            const tokenResponse = await fetch("http://localhost:8080/me", {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                }
            });

            if(tokenResponse.ok)
            {
                const tokenData = await tokenResponse.text();
                setUsername(tokenData);
            }
            else
                alert("TOKEN INVALID!!!");
        }
        checkToken();
    }, [token]);


    useEffect(() => {
        if (!username || !token) return;
    
        const getUserInfo = async () => {
          try {
            const userIdResponse = await fetch(
              `http://localhost:8080/username/${username}`,
              {
                headers: {
                  Authorization: `Bearer ${token}`,
                },
              }
            );
            if (!userIdResponse.ok) {
              alert(
                `Something went wrong getting user id ERROR CODE: ${userIdResponse.status}`
              );
            } else {
              const userInfoData = await userIdResponse.json();
              console.log(userInfoData);
            }
          } catch (err) {
            console.error('Error getting userId:', err);
          }
        };
        getUserInfo();
      }, [username, token]);



  return (
    <div>
        <label>Username: User1</label>
    </div>
  )
}

export default UserProfile
