import React, { useEffect, useState } from 'react'
import { getPosts } from '../../API/Axios';

export interface Post {
    post_id:number;
    goal_id:number;
    user_id:number;
    message_text:string;
    photo:any;

}

interface PostListProp {
    posts : Post[]
}
function PostList(prop: PostListProp) {

    function binaryStringToImage(binaryString : string) {
        // Convert binary string to Uint8Array
        const byteArray = new Uint8Array(binaryString.length / 8);
        for (let i = 0; i < binaryString.length; i += 8) {
          byteArray[i / 8] = parseInt(binaryString.slice(i, i + 8), 2);
        }
      
        // Create a Blob from the Uint8Array
        const blob = new Blob([byteArray], { type: 'image/jpeg' }); // Adjust type as needed
      
        // Create an Object URL from the Blob
        const url = URL.createObjectURL(blob);
      
        // Create an image element and set its source to the Object URL
        const img = new Image();
        img.src = url;
      
        // Return the image element
        return img;
      }
    
    return (
      <div>
          <h3>Consumed Messages: </h3>
                  {prop.posts.map((post,index) => (
                      <div>
                        <h4>Post ID: {post.post_id}</h4>
                        <h5>Goal ID: {post.goal_id}</h5>
                        <p>User ID: {post.user_id}</p>
                        <p>Messge Text: {post.message_text}</p>
                         binaryStringToImage(post.photo)
                        </div>
                  ))}

      </div>
      )
    }
  
  export default PostList
