import React, { useEffect, useState } from 'react'
import { getPosts } from '../../API/Axios';

export interface Post {
    // post_id:number;
    goal_id:number;
    user_id:number;
    message_text:string;
    photo: string;

}

interface PostListProp {
    posts : Post[]
}
function PostList(prop: PostListProp) {
    console.log(prop.posts);
    function binaryStringToImage(binaryData : string) {
            const binaryString = atob(binaryData); // Decoding the base64 binary string
            const byteArray = new Uint8Array(binaryString.length);
      
            // Convert the binary string to a byte array
            for (let i = 0; i < binaryString.length; i++) {
              byteArray[i] = binaryString.charCodeAt(i);
            }
      
            // Create a Blob from the byte array and use Object URL
            const blob = new Blob([byteArray], { type: 'image/png' }); // Update the MIME type if needed (e.g., 'image/jpeg')
            const url = URL.createObjectURL(blob);
            
            return url;
        }
    return (
      <div>
          <h3>Consumed Messages: </h3>
            <div>
            {prop.posts.map((post,index) => (
                      <div key = {index}>
                        <h5>Goal ID: {post.goal_id}</h5>
                        <p>User ID: {post.user_id}</p>
                        <p>Messge Text: {post.message_text}</p>
                        <img src = {post.photo}></img>
                        </div>
                  ))}
            </div>
      </div>
      )
    }
  
  export default PostList
