import axios from "axios";


// export interface Post {
//     goal_id:number;
//     user_id:number;
//     message_text:string;
//     photo:File|undefined;

// }
export const sendPost = async (formData : FormData) => {
    try {
        let goalId = formData.get("goal_id")?.valueOf();
        let userId = formData.get("user_id")?.valueOf();
        let messageText = formData.get("message_text")?.valueOf();

        const url = `http://localhost:8080/posts?goal_id=${goalId}&user_id=${userId}&message_text=${encodeURIComponent(messageText as string)}`;

        await axios.post(url, formData, {
            headers: { "Content-Type": "multipart/form-data" },
            withCredentials: true  // Important for sending cookies (session management)
        });
        console.log("Message sent successfully!");
    } catch (error) {
        console.error("Error sending message:", error);
    }
};
