import React, { useState } from 'react'
import PostFeedDumb from './PostFeedDumb';
import { useRef } from 'react';
import { Post, sendMessage } from '../../API/Axios';


function PostFeedSmart() {
    const [message, setMessage] = useState("");
    const [goalId, setGoalId] = useState(0);
    const [user, setUser] = useState(0);
    const form = useRef<HTMLFormElement | undefined>(undefined); //more react friendly way of getting document objects
    

    const handleSubmit = async (e : any) => {
        e.preventDefault();
        const formData = new FormData(form.current);
        console.log(formData.get("img_url")?.valueOf())
        let post : Post = {
            goal_id : formData.get("goal_id")?.valueOf() as number,
            user_id : formData.get("sent_by")?.valueOf() as number,
            message_text : formData.get("message_text")?.valueOf() as string,
            photo : formData.get("img_url")?.valueOf() as Blob
        }
        // await sendMessage(post);
    }

    return (
        <div>
            <PostFeedDumb formRef = {form} setMessage = {setMessage} setGoalId= {setGoalId} setUser = {setUser} onSubmit = {handleSubmit}></PostFeedDumb>
        </div>
    )
}
export default PostFeedSmart;
