    const [message, setMessage] = useState("");
    const [goalId, setGoalId] = useState(0);
    const [user, setUser] = useState(0);
    const [file, setFile] = useState<File | undefined>(undefined);


    const handleSubmit = async (e : any) => {
        e.preventDefault();
        
        let post : Post = {
            goal_id:goalId,
            user_id:user,
            message_text:message,
            photo:file
        }

        console.log(post);
