import axios from "axios";



export const sendPost = async (formData : FormData) => {
    try {
        const url = "http://localhost:8080/api/posts";

        await axios.post(url, formData, {
            headers: { "Content-Type": "multipart/form-data"
            }
        });
        console.log("Message sent successfully!");
    } catch (error) {
        console.error("Error sending message:", error);
    }
};

export const getPosts = async () => {
    try {
        const url = "http://localhost:8080/api/posts";


        let result = await axios.get(url, {
		headers: {"Content-Type": "application/json"
		}
	});
        if(result && result.status === 200)
        {
            console.log("Post Retrieved!");
            console.log(result.data);
            return result.data;
        }
        else{
            throw new Error;
        }
        
    } catch (error : any) {
        console.error("Error retrieving message:", error.getMessage());
    }
};

export const getTrackers = async (userId, goalId) => {
	try {
	 const url = 'http://localhost:8080/Tracker/${userId}/${goalId}';

	 let result = await axios.get(url, {
		 headers: {"Content-Type": "application/json"
		 }
	 });
	 if(result && result.status == 200)
	{
	   console.log("Trackers received");
	   console.log(result.data);
	   return result.data;
	}
	else {
	   throw new Error;
	}

	}
	catch(error : any) {
	   console.error("Error retrieving trackers: ", error.getMessage());
	}
}
