import {useEffect, useState} from 'react';
import {getGoalsbyUserId, usernameifAuthorized, getUserByUsername} from '../../API/Axios';


interface Exercise {
	duration : number;
	volume : number;
	calories : number;
	ExerciseDate : Date;
};

interface Nutrition {
	Kal : number;
	fat : number;
	carb : number;
	weight : number;
	protein : number;
	NutritionDate : Date;
};

interface Goal {
	id : number;
	user_id : number;
	createdAt : Date;
	sleep : number;
	water : number;
	sleepDate : Date;
	waterDate : Date;
	exercise : Exercise;
	nutrition : Nutrition;
}
function NotificationList()
{
	const [goals, setGoals] = useState<Goal[]>([]);
	const today = new Date();
	let tempGoals : Goal[] = [];
	const todayRes = new Date(today.getFullYear(), today.getMonth(), today.getDay());

	const getGoals = async () => {
		let username = await usernameifAuthorized();
		let user = await getUserByUsername(username);
		let goalList = await getGoalsbyUserId(user.id);
		return goalList;
	}
		
	useEffect( () => {
	
	getGoals().then ( (data) => {

		tempGoals = data.filter( (goal : Goal) => {
			let goalDate = goal.waterDate.getTime();
			let diff = today.getTime() - goalDate;

			if (diff >= 0)
			{
				console.log(goal);
				return goal;
			}
		})
	})

	},[])
	console.log(tempGoals);
	return (
		<div>
		{goals.map((goal : Goal, index : number) => (
		<div key = {index}>
			<h5> Goal ID : {goal.id} </h5>
			<h5> Goal Date : {(goal.waterDate).toString()} </h5>
		</div>
		))}
		</div>
	)
}
export default NotificationList;
