import {useState, useEffect, useRef} from 'react'
import {getUserByUsername, usernameifAuthorized, getTrackers} from '../../API/Axios'
import {Tracker, Goal, Exercise, Nutrition} from './Inbox';
import {Line} from 'react-chartjs-2';
import {Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend} from 'chart.js';
import PostFeedSmart from '../PostFeed/PostFeedSmart'

interface viewerProp {
	goalId : number;
	clicked : boolean;
}

function NotiViewer(prop : viewerProp)
{

	ChartJS.register(
		CategoryScale,
		LinearScale,
		PointElement,
		LineElement,
		Title,
		Tooltip,
		Legend
	);

	// Get Trackers for that Goal
	const [trackers, setTrackers] = useState([]);
	const [view, setView] = useState(false);

	const getData = async () => {
		
		let username = await usernameifAuthorized();
		let user = await getUserByUsername(username);
		let trackers = await getTrackers(user.id, prop.goalId);
		return trackers;
	}
	console.log(getData());
	// Separate the Data into Arrays for each Value Tracked::
	let data = getData();
	const datavis : {
	[key : string] : number[] | Date[];
	trackDate : Date[];
	burnedCal : number[];
	duration : number[];
	volume : number[];
	carb: number[];
	fat: number[];	
	kal: number[];
	protein: number[];		
	weight: number[];
	sleep: number[];
	water: number[]
	} = {
		trackDate : [],
		burnedCal:[],
		duration:[],
		volume:[],
		carb:[],
		fat:[],
		kal:[],
		protein:[],
		weight:[],
		sleep:[],
		water:[]
	}
	
	data.then( (trackerList : Object[]) => {
		trackerList.map( (tracker : any) => {
		datavis.trackDate.push(tracker.createdAt);
		datavis.burnedCal.push(tracker.exercise.caloriesBurned);
		datavis.duration.push(tracker.exercise.duration);
		datavis.volume.push(tracker.exercise.volume);
		datavis.carb.push(tracker.nutrition.carb);
		datavis.fat.push(tracker.nutrition.fat);
		datavis.kal.push(tracker.nutrition.kal);
		datavis.protein.push(tracker.nutrition.protein);
		datavis.weight.push(tracker.nutrition.weight);
		datavis.sleep.push(tracker.sleep);
		datavis.water.push(tracker.water);
		console.log(tracker.exercise.caloriesBurned);
		})
		console.log("Tracker Data: " + datavis.sleep);
	})

	const charts = Object.keys(datavis).filter((key)=> key != "trackDate").map((key) =>
		{
			console.log(key)
			const chartData = {
				labels:datavis.trackDate,
				datasets: [
					{
					label: key,
					data: datavis[key],
					fill: false,
					},
				],
			}
			const chartOptions = {
				responsive: true,
				scales : {
					x: {
					type : 'category' as const
					},
					y: {
					type : 'linear' as const
					}
				}
			}
			return (
				<div key = {key} >
				<h3>{key}</h3>
				<Line 
				data = {chartData}
				options={chartOptions}
				/>

				</div>
			);
		});


	return (	
	
		<>
		{prop.clicked && <div>{charts}</div>}
		{prop.clicked && <button onClick = {(e: any) => {setView(true)}}>Post about your Progress?</button>}
		{view && <PostFeedSmart />}
		</>
	);
}

export default NotiViewer
